import MuiDialogTitle from '@material-ui/core/DialogTitle'
import MuiDialogActions from '@material-ui/core/DialogActions'
import IconButton from '@material-ui/core/IconButton'
import CloseIcon from '@material-ui/icons/Close'
import Typography from '@material-ui/core/Typography'
import Select from '@material-ui/core/Select'
import TextField from '@material-ui/core/TextField'
import LanguageOptions from './LanguageOptions'
import { append, lensPath, path, pathOr, propEq, propOr, reject, view } from 'ramda'
import InputLabel from '@material-ui/core/InputLabel'
import FormControl from '@material-ui/core/FormControl'
import AddCircleIcon from '@material-ui/icons/AddCircle'
import RemoveCircleIcon from '@material-ui/icons/RemoveCircle'
import uuidv4 from 'uuid/v4'
import { useTranslation } from 'react-i18next'
import { getPaginationTypes } from '../utils/pagination-prediction'
import { Formik } from 'formik'
import { Buda } from '../../buda/types'

const styles = (theme: Theme) =>
    createStyles({
        root: {
            margin: 0,
            padding: theme.spacing(2),
        },
        closeButton: {
            position: 'absolute',
            right: theme.spacing(1),
            top: theme.spacing(1),
            color: theme.palette.grey[500],
        },
    })

const DialogTitle = withStyles(styles)(
    (props: {
        children: React.ReactElement
        classes: any
        onClose: () => void
    }) => {
        const { children, classes, onClose, ...other } = props
        return (
            <MuiDialogTitle
                disableTypography
                className={classes.root}
                {...other}
            >
                <Typography variant="h6">{children}</Typography>
                {onClose ? (
                    <IconButton
                        aria-label="close"
                        className={classes.closeButton}
                        onClick={onClose}
                    >
                        <CloseIcon />
                    </IconButton>
                ) : null}
            </MuiDialogTitle>
        )
    }
)

function SectionInput(props: {
    defaultLanguage?: any
    handleAddSection?: any
    handleRemoveSection?: any
    sectionInUseCount?: any
    key?: number
    new?: any
    data?: any
    i?: number
}) {
    const value = pathOr('', ['data', 'name', '@value'], props)
    const language = pathOr(
        props.defaultLanguage,
        ['data', 'name', '@language'],
        props
    )
    const id = pathOr(null, ['data', 'id'], props)
    const [sectionValue, setSectionValue] = React.useState(value)
    const [languageValue, setLanguageValue] = React.useState(language)
    const { defaultLanguage } = props
    const nameLanguage = path(['data', 'name', '@language'], props)

    React.useEffect(() => {
        if (!nameLanguage) {
            setLanguageValue(defaultLanguage)
        }
    }, [defaultLanguage, nameLanguage])

    const {
        handleAddSection,
        handleRemoveSection,
        sectionInUseCount,
        key,
    } = props
    const { t } = useTranslation()
    const inputValid = sectionValue.length > 0
    return (
        <div className="w-full flex mb-6" key={key}>
            <div className="w-1/2">
                <TextField
                    label={t('Section')}
                    type="text"
                    disabled={!props.new}
                    value={sectionValue}
                    style={{ width: '100%' }}
                    onChange={e => {
                        setSectionValue(e.target.value)
                    }}
                />
            </div>

            <FormControl>
                <InputLabel shrink> </InputLabel>
                <Select
                    native
                    disabled={!props.new}
                    value={languageValue}
                    onChange={e => {
                        setLanguageValue(e.target.value)
                    }}
                >
                    <LanguageOptions />
                </Select>
            </FormControl>

            <div className="w-1/4 flex items-center">
                {props.new ? (
                    <AddCircleIcon
                        style={{
                            color: inputValid ? 'black' : 'gray',
                            cursor: inputValid ? 'pointer' : 'initial',
                        }}
                        onClick={() => {
                            if (inputValid) {
                                setSectionValue('')
                                setLanguageValue(props.defaultLanguage)
                                handleAddSection(sectionValue, languageValue)
                            } else {
                                alert(t('Section name must not be empty!'))
                            }
                        }}
                    />
                ) : (
                    <RemoveCircleIcon
                        className="cursor-pointer"
                        onClick={() => {
                            const count = sectionInUseCount(id)
                            if (count > 0) {
                                alert(
                                    `${t('alert before count')} ${count} ${t(
                                        'alert after count'
                                    )}`
                                )
                            } else {
                                handleRemoveSection(id)
                            }
                        }}
                    />
                )}
            </div>
        </div>
    )
}

const DialogActions = withStyles(theme => ({
    root: {
        margin: 0,
        padding: theme.spacing(1),
    },
}))(MuiDialogActions)

export default function SettingsDialog(props: {
    open: boolean
    handleClose: any
    handleSettingsUpdate: any
    sectionInUseCount: any
    appData: any
    manifest: any
}) {
    const { handleSettingsUpdate, sectionInUseCount, appData, manifest } = props

    const handleAddSection = (value: string, language: string) => {
        const sectionsLens = lensPath(['sections'])
        const currentSections = view(
            sectionsLens,
            manifest
        ) as Buda.Manifest['sections'][]
        const updatedSections = append(
            // @ts-ignore
            { id: uuidv4(), name: { '@value': value, '@language': language } },
            currentSections
        )
        handleSettingsUpdate(sectionsLens, updatedSections)
    }

    const handleRemoveSection = (id: string) => {
        const sectionsLens = lensPath(['sections'])
        const currentSections = view(
            sectionsLens,
            manifest
        ) as Buda.Manifest['sections'][]
        // @ts-ignore
        const updatedSections = reject(propEq('id', id), currentSections)
        handleSettingsUpdate(sectionsLens, updatedSections)
    }

    const { t } = useTranslation()

    // @ts-ignore
    return (
        <Dialog
            onClose={props.handleClose}
            aria-labelledby="customized-dialog-title"
            open={props.open}
            fullWidth
        >
            <DialogTitle onClose={props.handleClose}>{t('Edit')}</DialogTitle>
            <div className="p-3">
                <div className="w-full">
                    <div className="w-2/4">
                        <FormControl style={{ width: '100%' }}>
                            <InputLabel shrink>{t('Status')}</InputLabel>
                            <Select
                                value={manifest['status']}
                                onChange={e => {
                                    handleSettingsUpdate(
                                        lensPath(['status']),
                                        e.target.value
                                    )
                                }}
                                native
                            >
                                <option value="editing">{t('editing')}</option>
                                <option value="released">
                                    {t('released')}
                                </option>
                                <option value="withdrawn">
                                    {t('withdrawn')}
                                </option>
                            </Select>
                        </FormControl>
                    </div>
                </div>
                <div>
                    <div className="w-full">
                        <div className="w-2/4">
                            <FormControl style={{ width: '100%' }}>
                                <InputLabel shrink>
                                    {t('Volume Language')}
                                </InputLabel>
                                <Select
                                    value={
                                        appData.bvmt['default-vol-string-lang']
                                    }
                                    onChange={e => {
                                        handleSettingsUpdate(
                                            lensPath([
                                                'appData',
                                                'bvmt',
                                                'default-vol-string-lang',
                                            ]),
                                            e.target.value
                                        )
                                    }}
                                    native
                                >
                                    <option value="bo"></option>
                                    <option value="en">English</option>
                                </Select>
                            </FormControl>
                        </div>
                    </div>
                </div>
                {/*<h2 className="mb-3 font-bold">{t('Input 1')}</h2>*/}
                <div className="w-full">
                    <div className="w-2/4">
                        <FormControl style={{ width: '100%' }}>
                            <InputLabel shrink>
                                {t('Pagination Type')}
                            </InputLabel>
                            <Select
                                value={path(
                                    ['pagination', 0, 'type'],
                                    manifest
                                )}
                                onChange={e => {
                                    handleSettingsUpdate(
                                        lensPath(['pagination', 0, 'type']),
                                        e.target.value
                                    )
                                }}
                                native
                            >
                                {getPaginationTypes().map((type, i) => (
                                    <option key={i} value="type">{t(type)}</option>
                                ))}
                            </Select>
                        </FormControl>
                    </div>
                </div>
                <div className="w-full my-4">
                    <div className="w-2/4">
                        <FormControl style={{ width: '100%' }}>
                            <InputLabel shrink>
                                {t('Viewing Direction')}
                            </InputLabel>
                            <Select
                                value={manifest['viewing-direction']}
                                onChange={e => {
                                    handleSettingsUpdate(
                                        lensPath(['viewing-direction']),
                                        e.target.value
                                    )
                                }}
                                native
                            >
                                <option value=""></option>
                                <option value="top-to-bottom">
                                    {t('top to bottom')}
                                </option>
                                <option value="left-to-right">
                                    {t('left to right')}
                                </option>
                                <option value="right-to-left">
                                    {t('right to left')}
                                </option>
                                <option value="bottom-to-top">
                                    {t('bottom to top')}
                                </option>
                                <option value="continuous">
                                    {t('continuous')}
                                </option>
                            </Select>
                        </FormControl>
                    </div>
                </div>
                {/*
                    // @ts-ignore */}
                {propOr([], 'sections', manifest).map((section, i) => {
                    return (
                        <SectionInput
                            i={i}
                            key={i}
                            data={section}
                            handleAddSection={handleAddSection}
                            handleRemoveSection={handleRemoveSection}
                            sectionInUseCount={sectionInUseCount}
                            defaultLanguage={
                                manifest.appData.bvmt['default-vol-string-lang']
                            }
                        />
                    )
                })}
                <SectionInput
                    new
                    handleAddSection={handleAddSection}
                    handleRemoveSection={handleRemoveSection}
                    sectionInUseCount={sectionInUseCount}
                    defaultLanguage={
                        manifest.appData.bvmt['default-vol-string-lang']
                    }
                />
                <div className="w-full">
                    <TextField
                        label={t('Indication (odd)')}
                        type="text"
                        defaultValue={appData.bvmt['margin-indication-odd']}
                        onBlur={e => {
                            handleSettingsUpdate(
                                lensPath([
                                    'appData',
                                    'bvmt',
                                    'margin-indication-odd',
                                ]),
                                e.target.value
                            )
                        }}
                        style={{ width: '50%' }}
                    />
                </div>
                <div className="w-full">
                    <TextField
                        label={t('Indication (even)')}
                        type="text"
                        defaultValue={appData.bvmt['margin-indication-even']}
                        style={{ width: '50%' }}
                        onBlur={e => {
                            handleSettingsUpdate(
                                lensPath([
                                    'appData',
                                    'bvmt',
                                    'margin-indication-even',
                                ]),
                                e.target.value
                            )
                        }}
                    />
                </div>
                <h3>{t('Notes')}</h3>
                <div className="w-full flex">
                    <Formik
                        initialValues={{
                            note: pathOr('', ['note', 0, '@value'], manifest),
                            language: pathOr(
                                manifest.appData['bvmt'][
                                    'default-ui-string-lang'
                                ],
                                ['note', 0, '@language'],
                                manifest
                            ),
                        }}
                        onSubmit={({ note, language }) => {
                            handleSettingsUpdate(lensPath(['note', 0]), {
                                '@value': note,
                                '@language': language,
                            })
                        }}
                        enableReinitialize
                    >
                        {({ values, handleChange, handleSubmit }) => (
                            <div className="w-full flex">
                                <div className="w-1/2">
                                    <TextField
                                        value={values.note}
                                        onChange={handleChange}
                                        style={{ width: '100%' }}
                                        inputProps={{
                                            id: 'note',
                                        }}
                                        multiline
                                        rows="4"
                                        // @ts-ignore
                                        onBlur={handleSubmit}
                                    />
                                </div>
                                <FormControl className="self-end">
                                    <Select
                                        native
                                        value={values.language}
                                        onChange={handleChange}
                                        // @ts-ignore
                                        onBlur={handleSubmit}
                                        inputProps={{
                                            id: 'language',
                                        }}
                                    >
                                        <LanguageOptions />
                                    </Select>
                                </FormControl>
                            </div>
                        )}
                    </Formik>
                </div>
                <DialogActions>
                    <Button
                        autoFocus
                        onClick={props.handleClose}
                        color="primary"
                    >
                        {t('OK')}
                    </Button>
                </DialogActions>
            </div>
        </Dialog>
    )
}
